
from PySteppables import *import CompuCellimport sysclass EMT_switch1Steppable(SteppableBasePy):        def __init__(self,_simulator,_frequency=1):        SteppableBasePy.__init__(self,_simulator,_frequency)            def start(self):        # any code in the start function runs before MCS=0        for cell in self.cellList:            if cell.type == self.EPITH:                cell.targetVolume = 100                cell.lambdaVolume = 50                            elif cell.type == self.FIB:                cell.targetVolume = 25                cell.lambdaVolume = 2                        elif cell.type == self.MES:                cell.targetVolume = 100                cell.lambdaVolume = 2                          def step(self,mcs):                #type here the code that will run every _frequency MCS        field = CompuCell.getConcentrationField(self.simulator, "Wnt")        comPt = CompuCell.Point3D()        for cell in self.cellList:            if cell.type == self.EPITH:  # Cell of type epith                comPt.x = int(round(cell.xCOM))                comPt.y = int(round(cell.yCOM))                comPt.z = int(round(cell.zCOM))                concentration = field.get(comPt)  # get concentration at comPt                threshold_conc = 0.8                if concentration > threshold_conc:                    cell.type = 3  # switch to cell type mes                                                    def finish(self):        # Finish Function gets called after the last MCS        pass        